    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
         Connection connection = null;
        PreparedStatement statement = null;
        String query = "UPDATE Produse SET pret_vanzare=? WHERE cod_articol=?";       
        try{
            connection = ConnectionFactory.getConnection();
            statement = connection.prepareStatement(query);
            statement.setString(1, jTextField2.getText());
            statement.setString(2, jTextField1.getText());
            statement.executeUpdate();
            JFrame frame = new JFrame();
            JOptionPane.showMessageDialog(frame, "This product was successfully modified");
            
        } catch (SQLException e) {
            e.printStackTrace();
        } finally {
            ConnectionFactory.close(statement);
            ConnectionFactory.close(connection);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

